export default function ProductCard({ product }) {
  return (
    <div className="border rounded p-4">
      <img src={product.imageUrl} alt={product.title} className="w-full h-40 object-cover mb-2" />
      <h3 className="text-lg font-bold">{product.title}</h3>
      <p className="text-gray-600">{product.type === "rent" ? "For Rent" : "For Sale"}</p>
      <p className="text-indigo-700 font-semibold">${product.price}</p>
      <p className="text-sm mt-1">{product.description}</p>
      <p className="text-sm text-gray-500 mt-2">Seller: {product.sellerEmail}</p>
    </div>
  );
}
