import { useEffect, useState } from "react";
import API from "../api";

export default function MyProducts() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    price: "",
    type: "sale",
    image: null,
  });

  const fetchMyProducts = async () => {
    try {
      const res = await API.get("/products/my");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchMyProducts();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure?")) return;
    try {
      await API.delete(`/products/${id}`);
      fetchMyProducts();
    } catch (err) {
      console.error(err);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product._id);
    setFormData({
      title: product.title,
      description: product.description,
      price: product.price,
      type: product.type,
      image: null, // Only update image if selected
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      let imageUrl = editingProduct.imageUrl;

      // If new image selected
      if (formData.image) {
        const imgData = new FormData();
        imgData.append("image", formData.image);
        const imgRes = await API.post("/upload", imgData);
        imageUrl = imgRes.data.imageUrl;
      }

      await API.put(`/products/${editingProduct}`, {
        title: formData.title,
        description: formData.description,
        price: formData.price,
        type: formData.type,
        imageUrl,
      });

      setEditingProduct(null);
      setFormData({ title: "", description: "", price: "", type: "sale", image: null });
      fetchMyProducts();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="mt-6">
      <h2 className="text-2xl font-bold mb-4">My Products</h2>
      {products.map((product) => (
        <div key={product._id} className="border p-4 mb-4 rounded shadow">
          {editingProduct === product._id ? (
            <form onSubmit={handleUpdate}>
              <input className="block w-full mb-2 p-2 border" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} required />
              <textarea className="block w-full mb-2 p-2 border" value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} />
              <input type="number" className="block w-full mb-2 p-2 border" value={formData.price} onChange={(e) => setFormData({ ...formData, price: e.target.value })} required />
              <select className="block w-full mb-2 p-2 border" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })}>
                <option value="sale">For Sale</option>
                <option value="rent">For Rent</option>
              </select>
              <input type="file" accept="image/*" onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })} />
              <button className="bg-blue-500 text-white px-4 py-2 rounded mr-2 mt-2">Update</button>
              <button className="bg-gray-400 px-4 py-2 rounded mt-2" type="button" onClick={() => setEditingProduct(null)}>Cancel</button>
            </form>
          ) : (
            <>
              <img src={product.imageUrl} alt={product.title} className="w-32 h-32 object-cover mb-2" />
              <h3 className="text-lg font-bold">{product.title}</h3>
              <p>{product.description}</p>
              <p>${product.price}</p>
              <p>{product.type}</p>
              <div className="mt-2">
                <button className="bg-yellow-500 text-white px-3 py-1 rounded mr-2" onClick={() => handleEdit(product)}>Edit</button>
                <button className="bg-red-500 text-white px-3 py-1 rounded" onClick={() => handleDelete(product._id)}>Delete</button>
              </div>
            </>
          )}
        </div>
      ))}
    </div>
  );
}
