import { useState } from "react";
import SignIn from "./components/SignIn";
import SignUp from "./components/SignUp";
import Home from "./pages/Home";
import MyProducts from "./components/MyProducts";
import AddProduct from "./pages/AddProduct";

function App() {
  const [user, setUser] = useState(localStorage.getItem("token") ? "user" : null);
  const [tab, setTab] = useState("home");

  const handleLogout = () => {
    localStorage.removeItem("token");
    setUser(null);
    setTab("home");
  };

  if (!user) {
    return (
      <div className="p-4">
        <SignIn onAuth={() => setUser("user")} />
        <hr className="my-4" />
        <SignUp onAuth={() => setUser("user")} />
      </div>
    );
  }

  return (
    <div className="p-4">
      <div className="flex gap-4 mb-4">
        <button onClick={() => setTab("home")} className="bg-indigo-500 text-white px-4 py-2 rounded">All Products</button>
        <button onClick={() => setTab("my")} className="bg-indigo-500 text-white px-4 py-2 rounded">My Products</button>
        <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">Logout</button>
      </div>
      {tab === "home" ? <Home /> : <MyProducts />}
    </div>
  );
}

export default App;
