import { useState } from "react";
import SignIn from "./components/SignIn";
import SignUp from "./components/SignUp";
import Home from "./pages/Home";

function App() {
  const [user, setUser] = useState(localStorage.getItem("token") ? "user" : null);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setUser(null);
  };

  return (
    <div className="p-4">
      {user ? (
        <>
          <Home />
          <button className="mt-4 bg-red-500 text-white p-2 rounded" onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <>
          <SignIn onAuth={() => setUser("user")} />
          <hr className="my-4" />
          <SignUp onAuth={() => setUser("user")} />
        </>
      )}
    </div>
  );
}

export default App;
